import React, { useState } from 'react';
import Modal from 'react-modal';
import './App.css';

function App() {
  const [boxes, setBoxes] = useState([]);
  const [selectedBoxId, setSelectedBoxId] = useState(null);
  const [newBoxText, setNewBoxText] = useState('');
  const [modalIsOpen, setModalIsOpen] = useState(false);

  const addBox = () => {
    const newBox = {
      id: boxes.length + 1,
      color: getRandomColor(),
      text: 'transformer_' + (boxes.length + 1),
    };
    setBoxes((prevBoxes) => [...prevBoxes, newBox]);
  };

  const removeBox = (id) => {
    const updatedBoxes = boxes.filter((box) => box.id !== id);
    setBoxes(updatedBoxes);
  };

  const openModal = (id, text) => {
    setSelectedBoxId(id);
    setNewBoxText(text);
    setModalIsOpen(true);
  };

  const closeModal = () => {
    setSelectedBoxId(null);
    setNewBoxText('');
    setModalIsOpen(false);
  };

  const handleInputChange = (event) => {
    setNewBoxText(event.target.value);
  };

  const updateBoxText = () => {
    const updatedBoxes = boxes.map((box) => {
      if (box.id === selectedBoxId) {
        return { ...box, text: newBoxText };
      }
      return box;
    });
    setBoxes(updatedBoxes);
    closeModal();
  };

  const getRandomColor = () => {
    const colors = ['green', 'yellow', 'purple', 'orange'];
    const randomIndex = Math.floor(Math.random() * colors.length);
    return colors[randomIndex];
  };

  return (
    <div className="App">
      <h1>Dynamic Boxes</h1>
      <button onClick={addBox}>Add Box</button>
      <div className="box-container">
        <div
          key="source"
          className="box"
          style={{ backgroundColor: 'white' }}
          onClick={() => openModal(box.id, box.text)}
        >
          <div className="box-text">Source</div>
        </div>
        {boxes.map((box) => (

          <>
            <div
                className="arrow"
                style={{
                  top: '5px',
                }} />
            <div
              key={box.id}
              className="box"
              style={{ backgroundColor: 'orange' }}
              onClick={() => openModal(box.id, box.text)}
              >
                {box.text}
              </div>

          </>
        ))}

        <div 
              className="arrow"
              style={{
              top: '5px',
              }}
            />

        <div
          key="source"
          className="box"
          style={{ backgroundColor: 'white' }}
        >
          <div className="box-text">Sync</div>
        </div>
        <Modal
          isOpen={modalIsOpen}
          onRequestClose={closeModal}
          contentLabel="Edit Box Text"
        >
          <h2>Edit Box Text</h2>
          <input
            type="text"
            value={newBoxText}
            onChange={handleInputChange}
          />
          <button onClick={updateBoxText}>Save</button>
          <button onClick={closeModal}>Cancel</button>
        </Modal>
      </div>
    </div>
  );
}

export default App;
