import React, { useState } from 'react';
import './App.css';

function App() {
  const [boxes, setBoxes] = useState([
    { id: 1, color: 'red' },
    { id: 2, color: 'blue' },
  ]);

  const addBox = () => {
    const newBox = {
      id: boxes.length + 1,
      color: getRandomColor(),
    };

    let newX = 60 * (boxes.length - 1);
    if (boxes.length === 1) {
      newX += 60;
    }

    setBoxes((prevBoxes) => [
      ...prevBoxes.slice(0, 1),
      { ...newBox, x: newX },
      ...prevBoxes.slice(1),
    ]);
  };

  const removeBox = (id) => {
    const updatedBoxes = boxes.filter((box) => box.id !== id);
    setBoxes(updatedBoxes);
  };

  const getRandomColor = () => {
    const colors = ['green', 'yellow', 'purple', 'orange'];
    const randomIndex = Math.floor(Math.random() * colors.length);
    return colors[randomIndex];
  };

  const renderArrows = () => {
    const arrows = [];
    for (let i = 1; i < boxes.length; i++) {
      const startX = 60 * (i - 1) + 40;
      const endX = 60 * i;
      const startY = 30;
      const endY = 30;
      const arrow = (
        <line
          key={i}
          x1={startX}
          y1={startY}
          x2={endX}
          y2={endY}
          stroke="black"
          markerEnd="url(#arrowhead)"
        />
      );
      arrows.push(arrow);
    }
    return arrows;
  };

  return (
    <div className="App">
      <h1>Dynamic Boxes</h1>
      <button onClick={addBox}>Add Box</button>
      <svg className="box-container">
        <defs>
          <marker
            id="arrowhead"
            markerWidth="10"
            markerHeight="7"
            refX="8"
            refY="3.5"
            orient="auto"
          >
            <polygon points="0 0, 10 3.5, 0 7" />
          </marker>
        </defs>
        {renderArrows()}
        {boxes.map((box) => (
          <rect
            key={box.id}
            className="box"
            x={60 * (box.id - 1)}
            y="10"
            width="40"
            height="40"
            fill={box.color}
            onClick={() => removeBox(box.id)}
          />
        ))}
      </svg>
    </div>
  );
}

export default App;
