import React, { useState } from 'react';
import './App.css';

function App() {
  const [boxes, setBoxes] = useState([]);
  const [editingBoxId, setEditingBoxId] = useState(null);
  const [newBoxText, setNewBoxText] = useState('');
  const [showPopup, setShowPopup] = useState(false);

  const addBox = () => {
    const newBox = {
      id: boxes.length + 1,
      color: getRandomColor(),
      text: 'transformer_' + (boxes.length + 1),
    };
    setBoxes((prevBoxes) => [...prevBoxes, newBox]);
  };

  const removeBox = (id) => {
    const updatedBoxes = boxes.filter((box) => box.id !== id);
    setBoxes(updatedBoxes);
  };

  const startEditing = (id, text) => {
    setEditingBoxId(id);
    setNewBoxText(text);
    setShowPopup(true);
  };

  const finishEditing = () => {
    setEditingBoxId(null);
    setNewBoxText('');
    setShowPopup(false);
  };

  const handleInputChange = (event) => {
    setNewBoxText(event.target.value);
  };

  const updateBoxText = (id) => {
    const updatedBoxes = boxes.map((box) => {
      if (box.id === id) {
        return { ...box, text: newBoxText };
      }
      return box;
    });
    setBoxes(updatedBoxes);
    finishEditing();
  };

  const getRandomColor = () => {
    const colors = ['green', 'yellow', 'purple', 'orange'];
    const randomIndex = Math.floor(Math.random() * colors.length);
    return colors[randomIndex];
  };



  return (
    <div className="App">
      <h1>Dynamic Boxes</h1>
      <button onClick={addBox}>Add Box</button>
      <div className="box-container">
        <div
          key="source"
          className="box"
          style={{ backgroundColor: 'white' }}
        >
          <div className="box-text">Source</div>
        </div>
        {boxes.map((box) => (

          <>
            <div
                className="arrow"
                style={{
                  top: '5px',
                }} />
            <div
              key={box.id}
              className="box"
              style={{ backgroundColor: 'orange' }}
              onClick={() => startEditing(box.id, box.text)}
            >
              {editingBoxId === box.id ? (
                <div className="box-popup">

                  {console.log(box.id)}
                  {console.log(boxes)}
                  <input
                    type="text"
                    value={newBoxText}
                    onChange={handleInputChange}
                  />
                  <button onClick={() => updateBoxText(box.id)}>Save</button>
                  <button onClick={finishEditing}>Cancel</button>
                </div>
              ) : (
                <div className="box-text">{box.text}</div>
              )}

            </div>
          </>
        ))}

        <div 
              className="arrow"
              style={{
              top: '5px',
              }}
            />

        <div
          key="source"
          className="box"
          style={{ backgroundColor: 'white' }}
        >
          <div className="box-text">Sync</div>
        </div>
      </div>
    </div>
  );
}

export default App;
